mapper.select:
select c.cmpnm, d.induty_id, d.mber_name, d.company_name, d.biz_num, b.mber_code, b.inout_no, b.inout_code, b.req_amt, b.balance, TO_DATE(b.req_dt || b.req_time , 'YYYYMMDDHH24MISS') as req_dt, b.charge, b.req_result, b.bank, b.account, b.depositor, b.bigo, b.biz_code, b.in_daily_sales_tot_amt, b.in_daily_sales_fee_per, b.in_daily_sales_fee_amt, b.in_daily_sales_dt, b.in_daily_sales_count, b.sales_tot_amt, b.sales_fee_per, b.sales_fee_amt, b.sales_dt, b.sales_time, b.sales_rcipt_no from mber a,tbl_atm b,biz c,mber_basis d where a.biz_code = c.biz_code and a.biz_code in (select biz_code from biz start with biz_code =#{memberBizeCode} connect by nocycle prior biz_code = trget_biz_code) and a.mber_code = b.mber_code and a.mber_code = d.mber_code and b.req_result = 'OK' -- 하드코딩 and b.req_dt between #{dateStart} and #{dateEnd}
AND A.biz_code= #{bizCode}
AND d.mber_name LIKE '%'||#{mberName}||'%'
AND d.induty_id LIKE '%'||#{indutyId}||'%'
AND d.company_name LIKE '%'||#{companyName}||'%'
AND d.biz_num LIKE '%'||#{bizNum}||'%'
--//입출금 구분 코드 (IN:입금, IN_DAILY:정기입금, IN_M:마이너스입금, OT:출금, OT_SF:관리비, OT_TF:통신비, OT_CANCEL : 결제취소건 출금, OT_UC : 본사출금)
AND b.inout_code LIKE '%'||#{inoutCode}||'%'
AND b.inout_code LIKE '%'||#{inoutCode}||'%'
AND b.inout_code LIKE '%'||#{inoutCode}||'%'
AND b.inout_code LIKE '%'||#{inoutCode}||'%'
AND b.inout_code = #{inoutCode}
AND b.inout_code = #{inoutCode}
AND b.inout_code = #{inoutCode}
AND b.inout_code = #{inoutCode}
AND b.inout_code = #{inoutCode}
order by b.req_dt desc,b.req_time desc,b.inout_no desc

SELECT a.mber_code, a.mber_code_sn, a.rcipt_no, SUBSTR(a.confm_dt,1,4)||'-'||SUBSTR(a.confm_dt,5,2)||'-'||SUBSTR(a.confm_dt,7,2) AS confm_dt, a.pymnt_yn, round(((a.splpc + a.vat)*(100-(SELECT fee_rate FROM mber_basis WHERE mber_code = c.mber_code)))/100) AS splpc, b.bank_name, b.account_no, b.depositor , case b.pay_type when 'N' then '일반' else ( case b.pay_type when 'B' then '바로정산' else '익일입금' end ) end as pay_type FROM DELNG a, MBER_BASIS b, MBER c WHERE a.mber_code = b.mber_code AND a.mber_code = c.mber_code AND a.card_delete_yn != 'Y' AND b.pay_type IN('N', 'T') -- 바로정산 제외... 결과적으로 위에 필요없는 코드가 있음. AND a.delng_se_code = 'CARD_ISSUE' -- 화면상 선택하는 부분이 없는데 널인경우 카드를 삽입함 AND a.confm_dt BETWEEN #{dateStart} and #{dateEnd} AND b.calculate_type='U' AND b.biz_code in (SELECT biz_code FROM biz START WITH biz_code = #{memberBizeCode} CONNECT BY NOCYCLE PRIOR biz_code = trget_biz_code)
AND b.biz_code= #{bizCode}
AND a.pymnt_yn=#{pymntYn}
AND b.mber_name LIKE '%'||#{mberName}||'%'
AND b.induty_id LIKE '%'||#{indutyId}||'%'
AND b.biz_num LIKE '%'||#{bizrno}||'%'
AND a.CONFM_NO LIKE '%'||#{confmNo}||'%'
AND b.pay_type = #{payType}
ORDER BY confm_dt DESC

SELECT to_char(to_Date(f.confm_dt), 'yyyy-mm-dd') As confm_dt_sub, to_char(to_Date(f.pay_dt), 'yyyy-mm-dd') As fpay_dt, case epay_type when 'T' then '익일입금' else '일반' end as pay_type, case fee_bank when 'P' then '500' else '0' end as bank_fee, case fee_bank when 'P' then seler_pymntamt - 500 else seler_pymntamt end as pay_amount, f.bank_name , f.account_no, f.depositor, f.cnt, f.pymnt_yn FROM ( SELECT b.pymnt_yn, Min(B.Confm_Dt) As Confm_Dt, Min(b.pay_dt) As pay_dt, e.bank_name, e.account_no, e.depositor, e.pay_type as epay_type, e.fee_bank, NVL(round(SUM(b.splpc+b.vat)-SUM(((b.splpc+b.vat)*(SELECT fee_rate FROM mber_basis WHERE mber_code = A.mber_code))/100)), 0) AS seler_pymntamt, COUNT(b.confm_dt) AS cnt FROM delng_credt a, (Select de.*, To_Char(To_Date(Next_Businessday_Func(To_Char(To_Date(de.Confm_Dt), 'yyyymmdd'), (select pay_type from mber_basis mb where mb.mber_code = de.mber_code))), 'yyyymmdd') as pay_dt from delng de) b, mber c, biz d, mber_basis e WHERE a.mber_code(+) = b.mber_code AND a.mber_code_sn(+) = b.mber_code_sn AND a.rcipt_no(+) = b.rcipt_no AND b.mber_code = c.mber_code AND c.biz_code = d.biz_code AND c.mber_code = e.mber_code AND b.card_delete_yn != 'Y' AND c.biz_code in (SELECT biz_code FROM biz START WITH biz_code = #{memberBizeCode} CONNECT BY NOCYCLE PRIOR biz_code = trget_biz_code) AND e.pay_type IN ('N','T') AND e.calculate_type='U' AND b.delng_se_code='CARD_ISSUE' AND b.pymnt_yn='N'
AND b.confm_dt BETWEEN #{dateStart} and #{dateEnd}
AND b.pay_dt BETWEEN #{dateStart} and #{dateEnd}
AND c.biz_code= #{bizCode}
AND b.pymnt_yn=#{pymntYn}
AND e.mber_name LIKE '%'||#{mberName}||'%'
AND e.induty_id LIKE '%'||#{indutyId}||'%'
AND e.biz_num LIKE '%'||#{bizrno}||'%'
GROUP BY b.pymnt_yn, c.usid, e.bank_name, e.account_no, e.mber_name, e.depositor, e.pay_type, e.fee_bank ) f Order By f.Confm_Dt Desc, f.depositor asc

mapper.update:
UPDATE tbl_config 
	SET 
enable_yn = #{enableYn} 
, msg = #{msg} 
WHERE gb_code = '000'

update.foreach:
  	UPDATE delng
	SET
	   pymnt_yn = #{autVo.pymntYn}
	WHERE
	        mber_code = #{autVo.mberCode}
	    AND mber_code_sn = #{autVo.mberCodeSn}
	    AND rcipt_no = #{autVo.rciptNo}
